  {
    "path": "src/templates.py",
    "description": "Holds the full default template contents for each initial artifact and provides helpers to render and validate them before writing to disk.",
    "category": "source"
  },
  {
    "path": "src/csv_utils.py",
    "description": "Provides robust CSV read/write utilities to create and validate the starter CASE_STUDIES_INDEX.csv intake table with required columns and proper quoting.",
    "category": "source"
  },
  {
    "path": "src/path_utils.py",
    "description": "Provides path resolution and filesystem safety utilities to ensure all writes stay under the project root and support dry-run behavior.",
    "category": "source"
  },
  {
    "path": "pyproject.toml",
    "description": "Defines the package metadata and an executable entry point for running the initializer as a command.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documentation explaining how to run the initializer, what files it creates under outputs, and how to extend the templates and metadata schema.",
    "category": "documentation"
  }
