[
  {
    "path": "scripts/project_tracker.py",
    "description": "Command-line script that creates and maintains a single source-of-truth progress ledger (JSON/CSV) by updating per-goal counts, recording per-cycle snapshots, and printing current active goals for each cycle to resolve the 'ACTUALLY PURSUED: 0' inconsistency.",
    "category": "source"
  },
  {
    "path": "outputs/PROJECT_TRACKER.json",
    "description": "Single source-of-truth progress ledger storing goals, per-cycle activity, and aggregate counts that the tracker script reads and updates deterministically.",
    "category": "support"
  },
  {
    "path": "outputs/PROJECT_TRACKER.csv",
    "description": "Optional human-readable/exportable mirror of the progress ledger with one row per goal per cycle and computed totals for easy inspection and spreadsheet use.",
    "category": "support"
  },
  {
    "path": "documentation/PROJECT_TRACKER.md",
    "description": "Usage documentation describing the ledger schema, supported commands (init/goal/cycle/update/status/export), and the intended workflow integration to keep counts consistent across cycles.",
    "category": "documentation"
  }
]

