    "path": "src/plan_project_scope_and_outline.py",
    "description": "Deterministic generator that creates runtime/outputs/plan_project_scope_and_outline.md and ensures it maps exactly to runtime/outputs/REPORT_OUTLINE.md and the section skeleton in runtime/outputs/DRAFT_REPORT_v0.md.",
    "category": "source"
  },
  {
    "path": "src/outline_schema.py",
    "description": "Defines the canonical outline data model, deterministic section ID generation, and rendering functions used to produce all three markdown outputs consistently.",
    "category": "source"
  },
  {
    "path": "src/md_render.py",
    "description": "Markdown rendering helpers that format headings, tables, checklists, and anchored section skeletons in a stable, deterministic way.",
    "category": "source"
  },
  {
    "path": "src/io_utils.py",
    "description": "File I/O utilities for atomic writes, directory creation, newline normalization, and deterministic content hashing to verify mapping.",
    "category": "source"
  },
  {
    "path": "src/cli.py",
    "description": "Command-line entrypoint that runs the generator with fixed defaults, validates deterministic mappings, and writes all required output files.",
    "category": "source"
  },
  {
    "path": "runtime/outputs/.gitkeep",
    "description": "Keeps the runtime/outputs directory present in version control for generated plan and report artifacts.",
    "category": "support"
  },
  {
    "path": "README.md",
    "description": "Usage documentation describing how to run the script to generate plan_project_scope_and_outline.md, REPORT_OUTLINE.md, and DRAFT_REPORT_v0.md with deterministic mapping guarantees.",
    "category": "documentation"
  }
]

