    "path": "templates/CASE_STUDY_TEMPLATE.md",
    "description": "Markdown template for creating new case studies with explicit sections that require linking each empirical claim to a claim card ID.",
    "category": "documentation"
  },
  {
    "path": "templates/CLAIM_CARD_TEMPLATE.md",
    "description": "Markdown template for individual claim cards containing fields for claim text, scope, evidence type, citations/DOIs/URLs, verification status, and abstention triggers.",
    "category": "documentation"
  },
  {
    "path": "config/claim_card_schema.json",
    "description": "JSON schema defining the required fields and allowed values for claim cards and the claim-to-case-study linkage rules.",
    "category": "config"
  },
  {
    "path": "src/claim_cards/__init__.py",
    "description": "Package initializer for claim card tooling used to validate and enforce the empirical-claim workflow.",
    "category": "source"
  },
  {
    "path": "src/claim_cards/parser.py",
    "description": "Parser utilities to extract structured fields from claim card markdown and to extract empirical-claim references from case study markdown.",
    "category": "source"
  },
  {
    "path": "src/claim_cards/validator.py",
    "description": "Validator that checks claim card files against the schema and enforces that any new empirical claim in the pilot case study is backed by a valid claim card.",
    "category": "source"
  },
  {
    "path": "src/claim_cards/cli.py",
    "description": "Command-line interface to run validation on the pilot case study and claim cards as a required check in the workflow.",
    "category": "source"
  },
  {
    "path": "scripts/validate_pilot_case_study.py",
    "description": "Standalone script wrapper that runs the claim-card validator specifically on the pilot case study paths for easy CI or local execution.",
    "category": "support"
  },
  {
    "path": "docs/claim_card_workflow.md",
    "description": "Documentation explaining how to create claim cards, how to reference them from the pilot case study, and how verification status and abstention triggers should be used.",
    "category": "documentation"
  }
]

