    "category": "source"
  },
  {
    "path": "src/outputs_scaffold/generator.py",
    "description": "Implements the end-to-end generator that creates /outputs and populates REPORT_OUTLINE.md, CASE_STUDY_TEMPLATE.md, METADATA_SCHEMA.* plus ensures RIGHTS_AND_LICENSING_CHECKLIST.md and RIGHTS_LOG.csv exist and are cross-referenced.",
    "category": "source"
  },
  {
    "path": "src/outputs_scaffold/templates.py",
    "description": "Contains embedded default template contents for all scaffolded artifacts to guarantee deterministic generation without external dependencies.",
    "category": "source"
  },
  {
    "path": "src/outputs_scaffold/cli.py",
    "description": "Provides a runnable CLI entry point to initialize or validate the outputs scaffold and report what was created or updated.",
    "category": "source"
  },
  {
    "path": "pyproject.toml",
    "description": "Defines the Python project configuration including an installable console script for running the outputs scaffold generator.",
    "category": "config"
  },
  {
    "path": "outputs/README.md",
    "description": "Documents the purpose of the /outputs directory, expected artifacts, and references to rights and licensing tracking files.",
    "category": "documentation"
  },
  {
    "path": "outputs/REPORT_OUTLINE.md",
    "description": "Provides the initial standardized report outline scaffold to guide report drafting and ensure consistent sections and citations.",
    "category": "documentation"
  },
  {
    "path": "outputs/CASE_STUDY_TEMPLATE.md",
    "description": "Provides a reusable case study template with prompts for context, evidence, methodology, outcomes, limitations, and rights notes.",
    "category": "documentation"
  },
  {
    "path": "outputs/METADATA_SCHEMA.json",
    "description": "Defines the canonical metadata schema for outputs artifacts in JSON Schema format including required fields and rights linkage.",
    "category": "documentation"
  },
  {
    "path": "outputs/RIGHTS_AND_LICENSING_CHECKLIST.md",
    "description": "Checklist for verifying permissions, attribution, and licensing requirements for any third-party assets referenced in outputs.",
    "category": "documentation"
  },
  {
    "path": "outputs/RIGHTS_LOG.csv",
    "description": "CSV template log to track rights, attribution, source URLs, license terms, and approval status for assets used in outputs.",
    "category": "support"
  }
]

