  {
    "path": "src/intake/normalize_task.py",
    "description": "Normalizes incoming intake JSON by filling defaults (including 2019–2025 date range) and deriving required query fields when DOI is missing before validation.",
    "category": "source"
  },
  {
    "path": "src/intake/validate_task.py",
    "description": "Provides a single entrypoint function and CLI to normalize then validate intake tasks against the checklist and JSON Schema with clear blocking error outputs.",
    "category": "source"
  },
  {
    "path": "src/intake/errors.py",
    "description": "Defines structured error types and helpers to format blocking validation failures for intake consumers and logs.",
