[
  {
    "path": "config/claim_card_schema.json",
    "description": "Defines the authoritative JSON Schema requiring verbatim claim text, context (speaker/date/link), and a provenance anchor with strict required-field rules.",
    "category": "config"
  },
  {
    "path": "src/claim_cards/models.py",
    "description": "Implements typed dataclasses/Pydantic-style models for claim cards and provenance metadata aligned to the schema for consistent parsing and validation.",
    "category": "source"
  },
  {
    "path": "src/claim_cards/validator.py",
    "description": "Provides full validation logic (schema + custom rules) and explicit abstention criteria when required fields are missing or non-verbatim/unsupported.",
    "category": "source"
  },
  {
    "path": "scripts/validate_outputs.py",
    "description": "CLI script to scan output claim cards (.md/.json), enforce the updated intake checklist requirements, and fail/abstain with actionable error reporting.",
    "category": "source"
  },
  {
    "path": "src/claim_cards/md_parser.py",
    "description": "Parses CLAIM_CARD_TEMPLATE.md-style markdown into structured objects, extracting verbatim claim, context fields, and provenance anchors reliably.",
    "category": "source"
  },
  {
    "path": "outputs/CLAIM_CARD_TEMPLATE.md",
    "description": "Updates the claim card template to mandate verbatim claim text plus context (speaker/date/link) and a provenance anchor field with clear instructions.",
    "category": "documentation"
  },
  {
    "path": "docs/intake_checklist.md",
    "description": "Documents the updated intake checklist requirements and abstention/validation rules for missing verbatim/context/provenance fields.",
    "category": "documentation"
  },
  {
    "path": "tests/test_claim_card_validation.py",
    "description": "End-to-end unit tests covering valid cards, missing required fields, non-verbatim claims, missing context, and provenance anchor abstention behavior.",
    "category": "test"
  },
  {
    "path": "tests/fixtures/claim_cards/valid_minimal.json",
    "description": "Provides a minimal valid claim card fixture including verbatim claim, full context (speaker/date/link), and provenance anchor for validation tests.",
    "category": "test"
  },
  {
    "path": "tests/fixtures/claim_cards/invalid_missing_fields.json",
    "description": "Provides an invalid claim card fixture missing required verbatim/context/provenance fields to verify abstention and error reporting.",
    "category": "test"
  }
]

