[
  {
    "path": "config/claim_card.schema.json",
    "description": "Defines the machine-readable JSON Schema for Claim Cards including required metadata, versioning, provenance, and correction history fields.",
    "category": "config"
  },
  {
    "path": "outputs/CLAIM_CARD_TEMPLATE.md",
    "description": "Provides the human-friendly Markdown Claim Card template with embedded YAML front matter aligned to the schema and containing all mandatory fields.",
    "category": "documentation"
  },
  {
    "path": "docs/CLAIM_CARD_WORKFLOW.md",
    "description": "Documents the end-to-end workflow for creating Claim Cards, validating them, running the 3-claim pilot, and logging failure modes and corrections.",
    "category": "documentation"
  },
  {
    "path": "src/claim_card/validate.py",
    "description": "Implements a CLI-capable validator that loads Claim Card YAML/JSON, validates against the schema, and emits structured failure-mode logs (missing metadata, version ambiguity, correction history issues).",
    "category": "source"
  },
  {
    "path": "src/claim_card/parse_md.py",
    "description": "Parses Markdown Claim Cards with YAML front matter into normalized JSON objects suitable for schema validation and pilot logging.",
    "category": "source"
  },
  {
    "path": "src/claim_card/run_pilot.py",
    "description": "Runs the 3-claim pilot by validating three sample Claim Cards, capturing validation outcomes, and writing a consolidated failure-mode report.",
    "category": "source"
  },
  {
    "path": "src/claim_card/failure_modes.py",
    "description": "Defines canonical failure-mode codes, detection rules, and helper routines to aggregate and summarize pilot issues consistently.",
    "category": "source"
  },
  {
    "path": "outputs/pilot/claim_001.md",
    "description": "Sample Claim Card #1 used in the 3-claim pilot containing a realistic claim and metadata to exercise validation and provenance requirements.",
    "category": "support"
  },
  {
    "path": "outputs/pilot/claim_002.md",
    "description": "Sample Claim Card #2 used in the 3-claim pilot intentionally containing version/provenance ambiguity to validate detection and logging.",
    "category": "support"
  },
  {
    "path": "outputs/pilot/claim_003.md",
    "description": "Sample Claim Card #3 used in the 3-claim pilot intentionally containing correction history gaps to validate detection and logging.",
    "category": "support"
  },
  {
    "path": "outputs/pilot/FAILURE_MODES_LOG.jsonl",
    "description": "Stores the machine-readable pilot run log as JSON Lines capturing per-claim validation results, detected failure modes, and suggested remediations.",
    "category": "support"
  }
]

