    "category": "source"
  },
  {
    "path": "src/qa/validator.py",
    "description": "Core validation logic for locating project root, invoking the generator deterministically, checking expected files (including rights artifacts), and producing structured results.",
    "category": "source"
  },
  {
    "path": "src/qa/reporting.py",
    "description": "Utilities to render validation results as a human-readable report and machine-readable JSON saved to outputs/qa/.",
    "category": "source"
  },
  {
    "path": "src/qa/paths.py",
    "description": "Path and filesystem helpers to resolve outputs directories, enforce deterministic locations, and support cross-platform execution.",
    "category": "source"
  },
  {
    "path": "src/qa/runner.py",
    "description": "Subprocess runner wrapper to execute the scaffold generator command with controlled environment, timeouts, and captured stdout/stderr.",
    "category": "source"
  },
  {
    "path": "configs/qa_expected_artifacts.json",
    "description": "Declarative list of required output artifacts and matching rules (exact filenames plus rights-artifact patterns) used by the validation harness.",
    "category": "config"
  },
  {
    "path": "README_QA.md",
