[
  {
    "path": "scripts/canonicalize_outputs.py",
    "description": "Standalone script that discovers agent-specific deliverables, migrates/copies them into runtime/outputs/ canonical subfolders, updates runtime/outputs/PROJECT_TRACKER.json to point at new paths, and generates runtime/outputs/CANONICALIZATION_REPORT.md with old-to-new mappings and actions taken.",
    "category": "source"
  },
  {
    "path": "scripts/lib/output_discovery.py",
    "description": "Library module that scans the repository for deliverables under known agent output roots (e.g., code-creation, document-creation, and other agent-stamped directories) and returns structured records with metadata used for canonicalization.",
    "category": "source"
  },
  {
    "path": "scripts/lib/path_canonicalizer.py",
    "description": "Library module that defines canonical destination rules under runtime/outputs/ (naming, collision handling, hashing, and folder taxonomy) and computes deterministic old->new path mappings.",
    "category": "source"
  },
  {
    "path": "scripts/lib/project_tracker_updater.py",
    "description": "Library module that loads runtime/outputs/PROJECT_TRACKER.json, rewrites tracked artifact paths to their canonical equivalents, preserves formatting/ordering where possible, and validates that all referenced files exist post-migration.",
    "category": "source"
  },
  {
    "path": "scripts/lib/report_writer.py",
    "description": "Library module that writes runtime/outputs/CANONICALIZATION_REPORT.md summarizing discovered artifacts, migration decisions (moved/copied/skipped), and a complete mapping table from old paths to new canonical paths.",
    "category": "source"
  },
  {
    "path": "scripts/lib/fs_ops.py",
    "description": "Shared filesystem utilities for safe copy/move, atomic writes, directory creation, checksum calculation, and dry-run support used by the canonicalization pipeline.",
    "category": "source"
  },
  {
    "path": "runtime/outputs/.gitkeep",
    "description": "Placeholder file ensuring the canonical runtime/outputs/ directory exists in version control for the migration target tree.",
    "category": "support"
  },
  {
    "path": "runtime/outputs/PROJECT_TRACKER.json",
    "description": "Canonical project tracker JSON file (created if missing) that indexes deliverables under runtime/outputs/ and is updated by the canonicalization script to reference migrated artifact paths.",
    "category": "config"
  },
  {
    "path": "runtime/outputs/CANONICALIZATION_REPORT.md",
    "description": "Generated report file (created if missing) documenting canonicalization results, including old->new path mappings and any errors or skipped items.",
    "category": "documentation"
  }
]

