[
  {
    "path": "outputs/catalog/README.md",
    "description": "Documents the catalog directory purpose, required files, and how the case study schema and CLI are used to create and index case studies.",
    "category": "documentation"
  },
  {
    "path": "outputs/catalog/METADATA_SCHEMA.json",
    "description": "Defines a JSON Schema for catalog metadata and case study records to enable consistent validation and tooling interoperability.",
    "category": "config"
  },
  {
    "path": "src/cli/add_case_study.py",
    "description": "Implements a Python CLI that creates outputs/case_studies/<slug>/case_study.json from inputs, validates it against the schema, and updates outputs/case_studies/index.json atomically.",
    "category": "source"
  },
  {
    "path": "src/catalog/schema.py",
    "description": "Provides schema loading and JSON validation helpers used by the CLI to validate case study documents against METADATA_SCHEMA.json.",
    "category": "source"
  },
  {
    "path": "src/catalog/index.py",
    "description": "Contains functions to read, update, sort, and write the case study index.json in a safe and deterministic manner.",
    "category": "source"
  },
  {
    "path": "src/catalog/paths.py",
    "description": "Centralizes filesystem path resolution for outputs/catalog and outputs/case_studies to keep CLI behavior consistent across environments.",
    "category": "source"
  },
  {
    "path": "src/templates/case_study_stub.json",
    "description": "Provides a minimal, valid case study JSON template used by the CLI as the starting structure when creating new case studies.",
    "category": "support"
  }
]

