  {
    "path": "scripts/canonicalize_outputs.py",
    "description": "Python CLI tool that scans agent-specific and runtime output locations, selects best-known deliverables, migrates them into canonical /outputs structure, and writes a detailed canonicalization report under outputs/tracking/ or outputs/qa/ including moves, collisions, and chosen winners.",
    "category": "source"
  },
  {
    "path": "scripts/_canonicalize_lib.py",
    "description": "Reusable library implementing discovery, scoring, collision resolution, safe move/copy, and report generation logic used by the canonicalization script.",
    "category": "source"
  },
  {
    "path": "scripts/qa_run_canonicalization.sh",
    "description": "Shell script that executes the canonicalization CLI with sensible defaults and exits nonzero on failure for QA/runtime automation.",
    "category": "support"
  },
  {
    "path": "outputs/tracking/.gitkeep",
    "description": "Placeholder file to ensure outputs/tracking directory exists for canonicalization reports in clean checkouts.",
    "category": "support"
  },
  {
    "path": "outputs/qa/.gitkeep",
    "description": "Placeholder file to ensure outputs/qa directory exists for canonicalization reports when QA mode is used.",
    "category": "support"
  }
]

