[
  {
    "path": "scripts/qa/qa_gate.py",
    "description": "Canonical QA gate entrypoint that attempts containerized execution first and transparently falls back to a no-container failsafe mode while always writing logs and partial results to /outputs/qa.",
    "category": "source"
  },
  {
    "path": "scripts/qa/docker_runner.py",
    "description": "Docker-based QA execution helper that runs the intended full QA command in a container, captures stdout/stderr and exit codes, and detects container-loss scenarios for failsafe triggering.",
    "category": "source"
  },
  {
    "path": "scripts/qa/failsafe_runner.py",
    "description": "No-container failsafe QA runner that executes a reduced, deterministic test subset locally with strict timeouts and structured error capture to ensure logs and partial results are produced.",
    "category": "source"
  },
  {
    "path": "scripts/qa/artifacts.py",
    "description": "Utility module for creating /outputs/qa directories and writing standardized artifacts (run metadata, logs, partial results, and error reports) in a consistent schema.",
    "category": "source"
  },
  {
    "path": "scripts/qa/commands.py",
    "description": "Command construction and environment normalization utilities that define the canonical container and failsafe command sets and ensure deterministic behavior across modes.",
    "category": "source"
  },
  {
    "path": "scripts/qa/detect.py",
    "description": "Environment and failure detection helpers that identify docker availability, container runtime health, and common 'container lost' patterns to decide when to degrade gracefully.",
    "category": "source"
  },
  {
    "path": "scripts/qa/README.md",
    "description": "Developer-facing documentation describing the QA gate behavior, artifacts produced, and how to extend the test sets for both container and failsafe modes.",
    "category": "documentation"
  },
  {
    "path": "outputs/qa/RUN_INSTRUCTIONS.md",
    "description": "Runbook documenting the single canonical QA gate command, expected outputs under /outputs/qa, and how to force docker or failsafe modes.",
    "category": "documentation"
  },
  {
    "path": "scripts/qa/defaults.yaml",
    "description": "Configuration file defining default docker image/args, full test command, failsafe reduced test selection, timeouts, and artifact paths for the QA gate.",
    "category": "config"
  }
]

