    "category": "source"
  },
  {
    "path": "tools/pipeline.py",
    "description": "Implements the one-command harness that runs init_outputs then validate_outputs, performs required artifact presence/minimal-completeness checks, and exits nonzero on any failure.",
    "category": "source"
  },
  {
    "path": "tools/qa_gate.py",
    "description": "Provides reusable orchestration helpers for running pipeline stages deterministically, capturing failures, and producing a concise QA gate report/exit code.",
    "category": "source"
  },
  {
    "path": "tools/qa_artifacts.py",
    "description": "Implements concrete checks for TRACKING_RECONCILIATION.md, Claim Cards, and QA gate artifacts including minimal completeness heuristics and canonical path handling.",
    "category": "source"
  },
  {
    "path": "scripts/run_pipeline.sh",
    "description": "Shell wrapper that invokes the canonical python QA gate command for local and CI usage with strict failure propagation.",
    "category": "support"
  },
  {
    "path": ".github/workflows/qa-gate.yml",
    "description": "CI workflow that runs the canonical QA gate command on pushes and pull requests and fails the build when required outputs/artifacts are missing or incomplete.",
    "category": "config"
  },
  {
    "path": "docs/QA_GATE.md",
    "description": "Documentation describing how to run the one-command QA gate locally and in CI, what artifacts it validates, and common failure modes.",
    "category": "documentation"
  }
]

