[
  {
    "path": "tools/qa_run.py",
    "description": "Single entrypoint script that generates scaffolding, validates required artifacts/content, fails fast on suggests fixes, and writes QA results into outputs/qa/.",
    "category": "source"
  },
  {
    "path": "src/scaffold.py",
    "description": "Scaffolding generator that creates required directories and materializes REPORT_OUTLINE.md, CASE_STUDY_TEMPLATE, and baseline QA report stubs from templates.",
    "category": "source"
  },
  {
    "path": "src/specs.py",
    "description": "Machine-checkable specification of required files plus content assertions (markers/sections) used by qa_run to validate the scaffold deterministically.",
    "category": "source"
  },
  {
    "path": "templates/REPORT_OUTLINE.md",
    "description": "Canonical report outline template used by the scaffolder and asserted by QA for required headings/markers.",
    "category": "documentation"
  },
  {
    "path": "templates/CASE_STUDY_TEMPLATE.md",
    "description": "Canonical case study template used by the scaffolder and asserted by QA for required sections/markers.",
    "category": "documentation"
  },
  {
    "path": "Makefile",
    "description": "Make targets to run the single QA pipeline (scaffold generation + assertions) via python tools/qa_run.py and ensure outputs/qa/ is produced.",
    "category": "config"
  },
  {
    "path": "outputs/qa/.gitkeep",
    "description": "Keeps the outputs/qa directory present in version control while QA reports are generated into it at runtime.",
    "category": "support"
  }
]

