[
  {
    "path": "src/case_studies/__init__.py",
    "description": "Defines the case_studies package and exposes versioned schema utilities for external callers.",
    "category": "source"
  },
  {
    "path": "src/case_studies/schema_v1.py",
    "description": "Implements the v1 metadata schema definition (fields, required/optional, enums) and a dependency-free validator.",
    "category": "source"
  },
  {
    "path": "src/case_studies/paths.py",
    "description": "Provides canonical path rules and helpers for locating the case-studies root and per-case folder layout.",
    "category": "source"
  },
  {
    "path": "src/case_studies/add.py",
    "description": "Implements the minimal CLI entrypoint (python -m case_studies.add) to scaffold a new case folder with prefilled metadata and validate it.",
    "category": "source"
  },
  {
    "path": "src/case_studies/validate.py",
    "description": "Implements a CLI (python -m case_studies.validate) to validate one or all case metadata files against the v1 schema for CI/hooks usage.",
    "category": "source"
  },
  {
    "path": "src/case_studies/index.py",
    "description": "Generates and/or validates an index of cases by scanning folders and loading metadata with schema checks.",
    "category": "source"
  },
  {
    "path": "support/githooks/pre-commit",
    "description": "Git pre-commit hook that runs python -m case_studies.validate and blocks commits when schema validation fails.",
    "category": "support"
  },
  {
    "path": "support/install_githooks.py",
    "description": "Installs the repository’s git hooks by copying support/githooks/pre-commit into .git/hooks with executable permissions.",
    "category": "support"
  }
]

