[
  {
    "path": "scripts/qa_run.sh",
    "description": "Single-entry CLI shell script that runs the QA pipeline by generating/collecting artifacts, validating required output paths, and producing /outputs/qa/qa_summary.json and /outputs/qa/qa_summary.md with run metadata.",
    "category": "support"
  },
  {
    "path": "qa/__init__.py",
    "description": "QA package initializer enabling python -m qa.run execution and exposing package version/metadata helpers.",
    "category": "source"
  },
  {
    "path": "qa/run.py",
    "description": "Main QA runner implementing artifact generation/collection, required path checks, metadata capture (time, git SHA, inputs), and writing qa_summary.json and qa_summary.md under outputs/qa.",
    "category": "source"
  },
  {
    "path": "qa/artifacts.py",
    "description": "Artifact generation/collection utilities invoked by the QA runner to produce and/or aggregate intermediate QA artifacts into canonical output locations.",
    "category": "source"
  },
  {
    "path": "qa/validate.py",
    "description": "Output validation module that checks required output paths, file presence, and basic integrity, returning structured results for inclusion in the QA summary.",
    "category": "source"
  },
  {
    "path": "qa/git.py",
    "description": "Small git metadata helper module to resolve repository SHA/dirty state safely even when git is unavailable (e.g., in CI artifacts).",
    "category": "source"
  },
  {
    "path": "qa/io.py",
    "description": "I/O helpers for creating directories, atomic JSON/Markdown writes, path normalization, and deterministic serialization for QA summary outputs.",
    "category": "source"
  },
  {
    "path": "scripts/qa/README.md",
    "description": "Usage documentation describing how to run scripts/qa_run.sh or python -m qa.run, expected inputs, produced outputs, and troubleshooting notes.",
    "category": "documentation"
  }
]

