[
  {
    "path": "scripts/qa_run.py",
    "description": "End-to-end QA runner that executes a minimal smoke test first, captures full stdout/stderr into canonical artifacts under outputs/qa/exec_logs/, detects failure signatures, performs automatic retry, and writes an environment dump (container image hash, resources, dependency versions) on retry-triggered failures.",
    "category": "source"
  },
  {
    "path": "scripts/qa_smoke_test.py",
    "description": "Minimal smoke test entrypoint designed to run first and quickly validate basic runtime assumptions (Python, filesystem, Docker availability) while emitting deterministic output for log capture.",
    "category": "source"
  },
  {
    "path": "scripts/env_dump.py",
    "description": "Environment introspection utility that collects container identity (image hash when available), host/container resources, OS details, and dependency versions and writes a structured dump to outputs/qa/exec_logs/ for failure triage.",
    "category": "source"
  },
  {
    "path": "scripts/failure_signatures.py",
    "description": "Centralized catalog and matching logic for known unstable/unsupported-environment failure signatures used to trigger automatic retry and env dump behavior.",
    "category": "source"
  },
  {
    "path": "scripts/exec_utils.py",
    "description": "Shared subprocess execution helpers that stream and tee stdout/stderr to canonical log files with timestamps, exit codes, and per-attempt metadata.",
    "category": "source"
  },
  {
    "path": "scripts/paths.py",
    "description": "Path canonicalization helpers that standardize output locations (including outputs/qa/exec_logs/) and ensure directory creation across local/CI/Docker runs.",
    "category": "source"
  },
  {
    "path": "docker/Dockerfile",
    "description": "Dockerfile that installs project dependencies and provides a consistent container environment for running the QA smoke test and full QA pipeline.",
    "category": "config"
  },
  {
    "path": "docker/entrypoint.sh",
    "description": "Container entrypoint that invokes the QA runner with sensible defaults and ensures logs and environment dumps are written under outputs/qa/exec_logs/.",
    "category": "support"
  },
  {
    "path": "docker-compose.yml",
    "description": "Docker Compose configuration to build and run the QA pipeline in a container with mounted outputs directory for canonical artifact capture.",
    "category": "config"
  }
]

