[
  {
    "path": "src/claims/intake_fields.py",
    "description": "Defines the required intake fields and their semantics (including claim text and dataset anchor) in a single, importable Python configuration module.",
    "category": "source"
  },
  {
    "path": "src/claims/intake_schema.py",
    "description": "Implements a machine-validated schema (e.g., JSON Schema as a Python dict) for claim card intake, encoding required fields and structural constraints.",
    "category": "source"
  },
  {
    "path": "src/claims/intake_validator.py",
    "description": "Provides hard-fail validation functions that reject claim cards missing required claim text or dataset anchor and emits clear error messages.",
    "category": "source"
  },
  {
    "path": "src/claims/templates.py",
    "description": "Contains example compliant vs non-compliant primary-source verification templates as structured strings/data for reuse in docs and tests.",
    "category": "support"
  },
  {
    "path": "docs/intake_checklist.md",
    "description": "Updates the intake checklist to codify claim text and dataset anchor as explicitly required fields and mirrors the validator hard-fail rules.",
    "category": "documentation"
  },
  {
    "path": "docs/CLAIM_CARD_TEMPLATE.md",
    "description": "Updates the claim card template to include required claim text and dataset anchor fields and adds an inline compliant vs non-compliant example section for primary-source verification.",
    "category": "documentation"
  },
  {
    "path": "docs/primary_source_verification_examples.md",
    "description": "Standalone examples showing compliant vs non-compliant inputs focused on primary-source verification, highlighting missing claim text or dataset anchor as hard failures.",
    "category": "documentation"
  },
  {
    "path": "tests/test_intake_validator_required_fields.py",
    "description": "Unit tests asserting the validator hard-fails when claim text or dataset anchor is missing and passes when both are present and well-formed.",
    "category": "test"
  }
]

