  {
    "path": "scripts/qa_artifacts.py",
    "description": "Defines the post-run artifact checklist, normalization utilities, and manifest schema/IO used by the QA runner and CI to verify and record produced artifacts.",
    "category": "source"
  },
  {
    "path": "scripts/qa_run.py",
    "description": "Single deterministic QA entrypoint that generates required scaffold, runs checks, verifies post-run artifacts (existence + minimal non-empty), and writes a normalized run manifest listing artifacts and paths.",
    "category": "source"
  },
  {
    "path": "scripts/validate_outputs.py",
    "description": "Validates the normalized run manifest and artifact checklist results for CI consumption, failing fast on missing/empty artifacts or malformed manifest.",
    "category": "source"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI workflow that runs the single QA runner, then validates the produced run manifest and artifact checklist outcomes as a post-run gate.",
    "category": "config"
  },
  {
    "path": "scripts/qa/README.md",
    "description": "Documents how to run the QA harness locally and in CI, what artifacts are expected, and the format/location of the normalized run manifest.",
    "category": "documentation"
  }
]

