[
  {
    "path": "scripts/ci_qa_harness.py",
    "description": "Deterministic CI/QA entrypoint that generates required scaffold, runs the canonical QA command, enforces the /outputs-only write policy, and writes QA_REPORT.json to /outputs.",
    "category": "source"
  },
  {
    "path": "scripts/validate_outputs.py",
    "description": "Utility that scans the repository after QA execution and fails if any new/modified files are written outside the canonical outputs tree with allowlist support.",
    "category": "source"
  },
  {
    "path": "scripts/scaffold_outputs.py",
    "description": "Creates and normalizes the canonical /outputs directory structure and required placeholder files to make the harness deterministic.",
    "category": "support"
  },
  {
    "path": ".github/workflows/qa-regression.yml",
    "description": "GitHub Actions workflow that runs the harness on every push/PR and on a scheduled cadence, uploads outputs/QA_REPORT.json as an artifact, and fails on QA gate or invalid writes.",
    "category": "config"
  },
  {
    "path": "config/qa_harness.json",
    "description": "Config declaring the canonical QA command, outputs root, write-policy allowlists, and report/artifact paths for consistent CI and local runs.",
    "category": "config"
  },
  {
    "path": "outputs/.gitkeep",
    "description": "Keeps the canonical outputs directory tracked so CI and local runs have a stable artifact root.",
    "category": "support"
  },
  {
    "path": "README_QA_HARNESS.md",
    "description": "Documentation describing how to run the CI/QA harness locally and in CI, interpret QA_REPORT.json, and troubleshoot write-policy failures.",
    "category": "documentation"
  }
]

