[
  {
    "path": "scripts/canonicalize_outputs.py",
    "description": "End-to-end canonicalization CLI that scans scattered agent/runtime output locations, selects one authoritative QA runner and one authoritative schema, copies/migrates chosen artifacts into a single canonical outputs/ tree, and regenerates outputs/ARTIFACT_INDEX.md with canonical paths only.",
    "category": "source"
  },
  {
    "path": "scripts/lib/canonicalize/__init__.py",
    "description": "Package initializer for canonicalization library code used by the canonicalize_outputs script.",
    "category": "source"
  },
  {
    "path": "scripts/lib/canonicalize/discovery.py",
    "description": "Filesystem discovery utilities that locate candidate QA runners, schema files, and output artifacts across agent/runtime directories with ignore rules and deterministic ordering.",
    "category": "source"
  },
  {
    "path": "scripts/lib/canonicalize/selection.py",
    "description": "Deterministic scoring and selection logic to choose a single authoritative QA runner and schema based on heuristics (recency, completeness, validation success, and naming conventions).",
    "category": "source"
  },
  {
    "path": "scripts/lib/canonicalize/migrate.py",
    "description": "Safe migration/copy routines that create the canonical outputs/ tree, copy selected artifacts with collision handling, and optionally prune superseded canonical files.",
    "category": "source"
  },
  {
    "path": "scripts/lib/canonicalize/indexer.py",
    "description": "Markdown index generator that produces a refreshed outputs/ARTIFACT_INDEX.md referencing only canonical outputs/ paths with stable sections and file metadata.",
    "category": "source"
  },
  {
    "path": "scripts/lib/canonicalize/reporting.py",
    "description": "Structured logging and JSON report helpers for summarizing scan results, selection decisions, and migration actions for CI-friendly diagnostics.",
    "category": "source"
  },
  {
    "path": "scripts/README_CANONICALIZE.md",
    "description": "Usage documentation for the canonicalization workflow including CLI examples, selection rules, expected directory layout, and troubleshooting notes.",
    "category": "documentation"
  }
]

