[
  {
    "path": "scripts/qa_run.py",
    "description": "Canonical QA runner that prefers Docker container execution but deterministically falls back to a failsafe no-container mode that always writes /outputs/qa/QA_REPORT.json and logs with stable exit codes.",
    "category": "source"
  },
  {
    "path": "scripts/qa_failsafe.py",
    "description": "Pure-Python failsafe QA implementation reusing existing checks (file presence, JSON schema validation, optional linkcheck) and producing a deterministic QA_REPORT payload for the main runner.",
    "category": "source"
  },
  {
    "path": "scripts/qa_docker.py",
    "description": "Docker integration helpers for the QA runner including container availability detection, robust invocation, log capture, and explicit error classification to trigger failsafe mode.",
    "category": "source"
  },
  {
    "path": "scripts/qa_report.py",
    "description": "Shared report/logging utilities that guarantee report directory creation, atomic JSON writing of QA_REPORT.json, and consistent exit-code mapping across container and failsafe modes.",
    "category": "source"
  },
  {
    "path": "scripts/qa_checks.py",
    "description": "Reusable check logic (expected output file presence, JSON parseability, schema validation via pure-Python, and linkcheck adapter) consumed by both container and no-container execution paths.",
    "category": "source"
  },
  {
    "path": "schemas/qa_report.schema.json",
    "description": "JSON Schema defining the required structure of /outputs/qa/QA_REPORT.json to ensure machine-validated, stable reporting across all execution environments.",
    "category": "config"
  },
  {
    "path": "scripts/vendor/jsonschema_min.py",
    "description": "Minimal pure-Python JSON Schema validator shim (supporting the subset used by this project) to avoid external dependencies in failsafe no-container mode.",
    "category": "support"
  }
]

