  {
    "path": "src/pipeline.py",
    "description": "Implements a runnable pipeline that creates canonical /outputs artifacts (index, run evidence, and a domain artifact stub) and re-attempts the survey task using the new artifact workflow.",
    "category": "source"
  },
  {
    "path": "src/artifacts.py",
    "description": "Provides artifact models and writers for the outputs index, run evidence, and domain artifact stub with deterministic paths and metadata.",
    "category": "source"
  },
  {
    "path": "src/survey_retry.py",
    "description": "Contains the blocked survey task re-attempt logic wired to read/write the canonical artifacts and record results into run evidence.",
    "category": "source"
  },
  {
    "path": "src/cli.py",
    "description": "Defines a command-line interface to run the pipeline once, generate concrete files, and trigger the survey retry step.",
    "category": "source"
  },
  {
    "path": "outputs/.gitkeep",
    "description": "Ensures the canonical outputs directory exists in version control for CI/CD runs.",
    "category": "support"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "Runs CI/CD to install dependencies, execute the pipeline once to generate outputs, and fail on errors while archiving outputs as workflow artifacts.",
    "category": "config"
  },
  {
    "path": "pyproject.toml",
    "description": "Defines the Python project metadata and minimal dependencies plus an entry point for the CLI.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documents how to run the pipeline locally and in CI/CD, and explains the canonical outputs artifacts produced by a run.",
    "category": "documentation"
  }
]

