[
  {
    "path": "pyproject.toml",
    "description": "Defines the Python package metadata, dependencies (e.g., numpy, matplotlib), and console script entrypoint for a deterministic run that writes fixed outputs.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documents how to install and run the deterministic entrypoint, describes the fixed output files and the metadata captured.",
    "category": "documentation"
  },
  {
    "path": "src/deterministic_runner/__init__.py",
    "description": "Exposes the public package API and version information for the deterministic runner library.",
    "category": "source"
  },
  {
    "path": "src/deterministic_runner/cli.py",
    "description": "Implements the deterministic command-line entrypoint that seeds RNGs, runs the computation, and writes /outputs/results.json and /outputs/figure.png with stable filenames.",
    "category": "source"
  },
  {
    "path": "src/deterministic_runner/determinism.py",
    "description": "Provides utilities to set deterministic behavior across random, numpy, and hash seeds while avoiding time-dependent behavior.",
    "category": "source"
  },
  {
    "path": "src/deterministic_runner/metadata.py",
    "description": "Collects a small reproducibility metadata block including git hash if available, Python version, and installed package versions.",
    "category": "source"
  },
  {
    "path": "src/deterministic_runner/io.py",
    "description": "Handles creation of the outputs directory and writing results.json with fixed keys and figure.png in a deterministic manner.",
    "category": "source"
  },
  {
    "path": "src/deterministic_runner/plotting.py",
    "description": "Generates a deterministic matplotlib figure saved to /outputs/figure.png with fixed styling and content derived from the run results.",
    "category": "source"
  }
]

