    "path": "src/pipeline.py",
    "description": "End-to-end single-cycle pipeline that generates outputs/results.json, outputs/figure.png, outputs/run.log, outputs/test.log, and outputs/STATUS.md deterministically.",
    "category": "source"
  },
  {
    "path": "src/plotting.py",
    "description": "Plotting helpers to render a canonical figure.png from the pipeline results with consistent styling and deterministic output.",
    "category": "source"
  },
  {
    "path": "src/io_utils.py",
    "description": "Utility functions for structured logging, JSON read/write, directory management, and reproducible timestamp/run metadata capture.",
    "category": "source"
  },
  {
    "path": "tests/test_pipeline_smoke.py",
    "description": "Smoke test that runs the pipeline and asserts that all required output artifacts are created and minimally valid.",
    "category": "test"
  },
  {
    "path": "scripts/run_evidence_pack.py",
    "description": "CLI entrypoint to run the pipeline end-to-end and write all evidence pack artifacts into the outputs directory.",
    "category": "support"
  },
  {
    "path": "scripts/ci_run.sh",
    "description": "CI script that installs dependencies, runs formatting checks (if available), executes tests, runs the evidence pack, and exits nonzero on failure.",
    "category": "support"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "GitHub Actions workflow implementing CI/CD to run tests and generate the evidence pack artifacts on each push and pull request.",
    "category": "config"
  },
  {
    "path": "requirements.txt",
    "description": "Pinned runtime and test dependencies required to run the pipeline and generate figure outputs in CI.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Project documentation explaining how to run the pipeline, where artifacts are written, and how CI/CD validates the evidence pack.",
    "category": "documentation"
  }
]

