  },
  {
    "path": "tests/test_sweep_io_plotting.py",
    "description": "Pytest suite verifying sweep reproducibility, CSV round-trips, and figure export creation without interactive backends.",
    "category": "test"
  },
  {
    "path": "pyproject.toml",
    "description": "Project configuration defining Python 3.11+ package metadata, dependencies (sympy/numpy/scipy/matplotlib/pandas/pytest), and pytest settings for reproducible execution.",
    "category": "config"
  }
]

