[
  {
    "path": "src/define_comprehensive_v1.py",
    "description": "Command-line tool that creates or updates a 'Comprehensive v1 Definition' section in outputs/roadmap_v1.md or a companion scope file by generating included/excluded subtopics, prioritization rules, and required outputs per subtopic from structured inputs.",
    "category": "source"
  },
  {
    "path": "src/definition_models.py",
    "description": "Dataclasses and validation logic for the Comprehensive v1 schema including subtopics, inclusion/exclusion flags, prioritization rules, and per-subtopic output requirements.",
    "category": "source"
  },
  {
    "path": "src/markdown_section_editor.py",
    "description": "Utility module for idempotently inserting, replacing, and extracting a named markdown section while preserving surrounding content and stable anchors.",
    "category": "support"
  },
  {
    "path": "src/sources_ingest.py",
    "description": "Parser for ingesting relevant context snippets and agent review notes into structured evidence used to seed or justify scope decisions in the definition output.",
    "category": "support"
  },
  {
    "path": "src/config/comprehensive_v1.schema.json",
    "description": "JSON Schema defining the Comprehensive v1 Definition structure to enable validation of scope configuration files and ensure required fields are present.",
    "category": "config"
  },
  {
    "path": "src/config/comprehensive_v1_defaults.json",
    "description": "Default configuration template specifying baseline included/excluded subtopics, prioritization rules, and required outputs per subtopic (e.g., theorem/example/sources) for v1 generation.",
    "category": "config"
  },
  {
    "path": "outputs/comprehensive_v1_scope.md",
    "description": "Companion scope file containing the full 'Comprehensive v1 Definition' section in markdown with explicit included/excluded subtopics, prioritization rules, and required outputs per subtopic.",
    "category": "documentation"
  },
  {
    "path": "tests/test_markdown_section_editor.py",
    "description": "Unit tests ensuring markdown section insertion and replacement are idempotent, correctly anchored, and do not corrupt unrelated content.",
    "category": "test"
  },
  {
    "path": "tests/test_definition_models.py",
    "description": "Unit tests for Comprehensive v1 schema validation including required outputs per subtopic and prioritization rule constraints.",
    "category": "test"
  },
  {
    "path": "README_comprehensive_v1.md",
    "description": "Developer documentation explaining how to run the generator, configure included/excluded subtopics and output requirements, and choose between updating outputs/roadmap_v1.md versus a companion scope file.",
    "category": "documentation"
  }
]

