[
  {
    "path": "src/results_schema.py",
    "description": "Defines the versioned results schema, provides runtime validation/coercion helpers, and exposes a single function to build a compliant /outputs/results.json payload.",
    "category": "source"
  },
  {
    "path": "src/determinism.py",
    "description": "Implements seed pinning across Python/numpy/random/torch (if present) and centralizes deterministic matplotlib plotting defaults to ensure reproducible figures.",
    "category": "source"
  },
  {
    "path": "src/results_io.py",
    "description": "Writes validated results.json and deterministic figure files to /outputs, embedding schema_version, run metadata, and content hashes for regression testing.",
    "category": "source"
  },
  {
    "path": "src/run_pipeline.py",
    "description": "End-to-end runnable pipeline entrypoint that applies determinism settings, executes the experiment(s), generates deterministic plots, and emits a schema-compliant outputs/results.json.",
    "category": "source"
  },
  {
    "path": "schemas/results.schema.v1.json",
    "description": "JSON Schema document for results.json v1 used for validation in code and tests.",
    "category": "config"
  },
  {
    "path": "tests/test_results_schema_validation.py",
    "description": "Pytest suite verifying results.json payloads validate against v1 schema and reject missing/incorrect fields while preserving schema_version behavior.",
    "category": "test"
  },
  {
    "path": "tests/test_determinism_seed_and_plotting.py",
    "description": "Pytest suite asserting fixed seeds and pinned matplotlib rcParams produce byte-stable or hash-stable figure outputs across repeated runs.",
    "category": "test"
  },
  {
    "path": "docs/RESULTS_SCHEMA.md",
    "description": "Documents the results.json stable schema, versioning policy, required fields, and determinism guarantees (seed + plotting parameters) for reproducible outputs.",
    "category": "documentation"
  }
]

