[
  {
    "path": "scripts/run_pipeline.py",
    "description": "Pipeline entrypoint updated to accept a single seed, propagate it through all RNGs, enforce writing to a canonical outputs/ layout, and validate outputs/results.json and outputs/figure.png against the fixed schema before exiting.",
    "category": "source"
  },
  {
    "path": "src/pipeline/output_schema.py",
    "description": "Defines the canonical results schema (fixed keys/types), provides validation utilities, and implements a single function to write outputs/results.json in a stable, deterministic form.",
    "category": "source"
  },
  {
    "path": "src/pipeline/seed_control.py",
    "description": "Implements unified seed control that sets and propagates one RNG seed across Python random, NumPy, and common ML/plotting libraries when available, returning a context object for downstream use.",
    "category": "source"
  },
  {
    "path": "src/pipeline/output_paths.py",
    "description": "Centralizes canonical output paths (outputs/results.json, outputs/figure.png, and optional metadata) and ensures directories exist with consistent behavior in local runs and CI.",
    "category": "source"
  },
  {
    "path": "docs/OUTPUTS_SCHEMA.md",
    "description": "Documents the enforced canonical outputs layout, the exact results.json keys and meanings, and how to run the pipeline with deterministic seeding.",
    "category": "documentation"
  },
  {
    "path": "tests/test_pipeline_outputs_schema.py",
    "description": "Automated tests that run the pipeline in a temp workspace to assert deterministic behavior under a fixed seed and validate that outputs/results.json and outputs/figure.png are produced and schema-compliant.",
    "category": "test"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI workflow that installs dependencies, runs unit tests, and executes the pipeline to enforce canonical outputs and deterministic seed behavior on every push and pull request.",
    "category": "config"
  }
]

