    "path": "src/io_contract.py",
    "description": "Defines and validates the fixed results contract (required keys, types, versioning metadata) for outputs/results.json and standardizes output writing.",
    "category": "source"
  },
  {
    "path": "src/plotting.py",
    "description": "Generates a deterministic figure.png (e.g., error distributions/summary bars) using a fixed style and no nondeterministic rendering settings.",
    "category": "source"
  },
  {
    "path": "requirements.txt",
    "description": "Pins Python dependencies needed to run the experiment and produce the PNG deterministically (e.g., numpy and matplotlib).",
