[
  {
    "path": "scripts/run_tests_and_capture_log.py",
    "description": "Runs the project's test command in a subprocess, captures stdout/stderr to a timestamped log under outputs/logs/, writes run_stamp.json, and updates outputs/index.md with links to the log and any results artifacts.",
    "category": "source"
  },
  {
    "path": "scripts/run_pipeline.py",
    "description": "Executes the project pipeline entrypoint (configurable command), captures stdout/stderr to outputs/logs/ with a timestamped log, writes run_stamp.json, and updates outputs/index.md to list the run log and produced results files.",
    "category": "source"
  },
  {
    "path": "scripts/_log_index_utils.py",
    "description": "Provides shared utilities for timestamped run stamps, creating outputs/logs directories, capturing subprocess output to log files, discovering results artifacts, and updating outputs/index.md deterministically.",
    "category": "support"
  },
  {
    "path": "outputs/index.md",
    "description": "Central index documenting the latest and historical run logs in outputs/logs/ along with run_stamp.json and discovered results files for CI/CD traceability.",
    "category": "documentation"
  },
  {
    "path": "roadmap_v1.md",
    "description": "Project roadmap updated to mark the test-and-log runner and pipeline runner as completed deliverables with links to outputs/index.md and the generated logs/artifacts.",
    "category": "documentation"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI workflow that checks out the repo, sets up Python, runs scripts/run_tests_and_capture_log.py and scripts/run_pipeline.py, and uploads outputs/ (logs, index, run_stamp, and results) as build artifacts.",
    "category": "config"
  }
]

