    "path": "src/pipeline/__init__.py",
    "description": "Defines the pipeline package and exposes the main entrypoint used by the project to run experiments end-to-end.",
    "category": "source"
  },
  {
    "path": "src/pipeline/entrypoint.py",
    "description": "Implements the pipeline entrypoint that runs the toy experiment with deterministic seeding and writes canonical artifacts under outputs/.",
    "category": "source"
  },
  {
    "path": "src/pipeline/artifacts.py",
    "description": "Provides reusable helpers for canonical artifact creation (outputs dir, results.json, figure.png, run_stamp.json, run.log) and atomic writes.",
    "category": "source"
  },
  {
    "path": "src/pipeline/seeding.py",
    "description": "Implements deterministic seeding across Python, NumPy, and (optionally) torch while recording the chosen seed for reproducibility.",
    "category": "source"
  },
  {
    "path": "src/experiments/toy_experiment.py",
    "description": "Contains the toy experiment logic (fixed syntax, deterministic behavior) producing metrics and an optional plot saved as figure.png.",
    "category": "source"
  },
  {
    "path": "src/__main__.py",
    "description": "Provides a CLI module so the pipeline entrypoint can be invoked via python -m src with configurable seed and output directory.",
    "category": "source"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "Runs CI on push/pull_request to execute the pipeline, verify canonical artifacts are created, and fail fast on reproducibility regressions.",
    "category": "config"
  },
  {
    "path": "scripts/run_pipeline.sh",
    "description": "Shell wrapper to run the pipeline deterministically in local/dev and CI environments while standardizing output location to outputs/.",
    "category": "support"
  }
]

