    "path": "automation_script.json",
    "description": "Defines an automation workflow to generate outputs/coverage_matrix.csv, outputs/eval_loop.md, and update outputs/index.md and outputs/manifest.json to link them.",
    "category": "config"
  },
  {
    "path": "outputs/coverage_matrix.csv",
    "description": "Provides the initial coverage matrix with stable ontology columns (domain, subtopic, artifact_types, status, cycle_target, links) and an initial row set for tracking work across cycles.",
    "category": "support"
  },
  {
    "path": "outputs/eval_loop.md",
    "description": "Specifies the per-cycle evaluation and shipping loop with explicit shipping rules and acceptance criteria for each cycle.",
    "category": "documentation"
  },
  {
    "path": "outputs/index.md",
    "description": "Acts as a human-readable outputs landing page that links to the coverage matrix and eval loop documentation.",
    "category": "documentation"
  },
  {
    "path": "outputs/manifest.json",
    "description": "Records a machine-readable manifest of output artifacts including metadata and links to coverage_matrix.csv and eval_loop.md.",
    "category": "config"
  }
]

