[
  {
    "path": "src/artifacts/schema.py",
    "description": "Defines the standard artifact schema for results.json (required/optional keys), figure naming conventions, and metadata fields including git commit, seed, and timestamps with validation helpers.",
    "category": "source"
  },
  {
    "path": "src/artifacts/writer.py",
    "description": "Implements a complete artifact writer that assembles metadata (git commit/dirty state, seed, ISO timestamps), enforces the schema, and writes results.json plus provides helpers for deterministic figure/file naming.",
    "category": "source"
  },
  {
    "path": "src/artifacts/git_utils.py",
    "description": "Provides robust utilities for obtaining git commit hash, branch, and dirty status with safe fallbacks for non-git environments.",
    "category": "source"
  },
  {
    "path": "src/artifacts/time_utils.py",
    "description": "Provides timestamp utilities (UTC ISO8601) used consistently across artifact metadata for start/end times and creation time.",
    "category": "support"
  },
  {
    "path": "src/artifacts/seed_utils.py",
    "description": "Centralizes seed handling and reproducibility helpers to set and record seeds consistently across runs and in results.json metadata.",
    "category": "support"
  },
  {
    "path": "outputs/roadmap_v1.md",
    "description": "Updates the DoD checklist by adding a new 'Determinism/Replay' subsection describing required artifact schema compliance and replay-critical metadata fields.",
    "category": "documentation"
  }
]

