[
  {
    "path": ".github/workflows/pipeline-ci.yml",
    "description": "GitHub Actions CI workflow that fresh-clones, installs dependencies, runs scripts/run_pipeline.py, validates required outputs via checksum/golden comparison, and gates merges on success.",
    "category": "config"
  },
  {
    "path": "scripts/ci/validate_artifacts.py",
    "description": "Artifact validation script that asserts required files exist under outputs/ and verifies determinism by comparing computed checksums to a tracked golden manifest.",
    "category": "source"
  },
  {
    "path": "outputs/golden_manifest.json",
    "description": "Golden checksum manifest for required pipeline artifacts under outputs/ used by CI to enforce deterministic builds.",
    "category": "support"
  },
  {
    "path": "scripts/ci/README.md",
    "description": "Documentation describing how the CI pipeline runs, which artifacts are required, how determinism is enforced, and how to update the golden manifest intentionally.",
    "category": "documentation"
  }
]

