[
  {
    "path": "scripts/reproducibility_check.js",
    "description": "Node.js script that runs the pipeline twice, computes SHA256 hashes for all artifacts under outputs/, compares runs and/or validates against outputs/hashes.json, and exits nonzero on any mismatch for CI/CD or local use.",
    "category": "source"
  },
  {
    "path": "scripts/hash_outputs.js",
    "description": "Node.js helper script that deterministically enumerates files in outputs/ and writes a stable outputs/hashes.json mapping of relative paths to SHA256 hashes to support regression checks.",
    "category": "source"
  },
  {
    "path": "outputs/hashes.json",
    "description": "In-repo canonical JSON file storing expected SHA256 hashes for artifacts in outputs/ to enable deterministic regression verification in CI/CD and local runs.",
    "category": "config"
  },
  {
    "path": ".github/workflows/reproducibility.yml",
    "description": "GitHub Actions workflow that runs the project pipeline and then executes the reproducibility check to fail CI on non-reproducible outputs or hash regressions.",
    "category": "config"
  },
  {
    "path": "Makefile",
    "description": "Make targets for running the pipeline, updating outputs/hashes.json, and performing reproducibility checks locally in a CI-aligned way.",
    "category": "config"
  },
  {
    "path": "README_reproducibility.md",
    "description": "Documentation describing how to run the reproducibility check locally/CI, how hashes are generated/updated, and how to diagnose mismatches.",
    "category": "documentation"
  }
]

