    "path": "pyproject.toml",
    "description": "Project build and tool configuration (Python 3.11+, packaging metadata, pytest settings) to support reproducible local and CI/CD runs.",
    "category": "config"
  },
  {
    "path": "src/blessed_pipeline/__init__.py",
    "description": "Package entrypoint exporting the blessed pipeline API as the single supported implementation path.",
    "category": "source"
  },
  {
    "path": "src/blessed_pipeline/pipeline.py",
    "description": "Blessed end-to-end pipeline implementation with corrected syntax and a stable public interface used by tests and CLI.",
    "category": "source"
  },
  {
    "path": "src/blessed_pipeline/cli.py",
    "description": "Command-line interface to run the blessed pipeline and optionally emit artifacts needed for integration validation.",
    "category": "source"
  },
  {
    "path": "tests/test_pipeline.py",
    "description": "Pytest suite that exercises the blessed pipeline API and guards against regressions that previously surfaced as syntax_error validation failures.",
    "category": "test"
  },
  {
    "path": "scripts/run_tests.py",
    "description": "Support script that runs the test suite and writes a full transcript to outputs/test_run.log for the mission’s required artifact.",
    "category": "support"
  },
  {
    "path": "outputs/test_run.log",
    "description": "Tracked test-run transcript artifact generated/overwritten by scripts/run_tests.py and CI to satisfy the requirement to store outputs/test_run.log.",
    "category": "support"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI/CD workflow that installs the project, runs scripts/run_tests.py, and uploads outputs/test_run.log as a build artifact.",
    "category": "config"
  }
]

