[
  {
    "path": "src/run.py",
    "description": "Single deterministic CLI entrypoint that sets all RNG seeds, runs the canonical pipeline, writes outputs/results.json and outputs/figure.png with fixed schema, and writes outputs/hashes.json with SHA256 hashes of each artifact.",
    "category": "source"
  },
  {
    "path": "src/pipeline.py",
    "description": "Implements the canonical pipeline steps (data generation/loading, processing, metrics computation, and figure creation) as pure functions to support deterministic execution from the CLI.",
    "category": "source"
  },
  {
    "path": "src/determinism.py",
    "description": "Utilities to set seeds across random/numpy/optional torch, enforce deterministic settings, and compute SHA256 hashes for produced artifacts.",
    "category": "source"
  },
  {
    "path": "src/io_utils.py",
    "description": "Helpers for atomic output writing, JSON schema-stable serialization, and ensuring the outputs directory exists.",
    "category": "source"
  },
  {
    "path": "tests/test_cli_outputs.py",
    "description": "End-to-end test that runs the CLI, verifies the existence and schema of results.json, existence of figure.png, and correctness/consistency of hashes.json across repeated runs.",
    "category": "test"
  },
  {
    "path": "tests/test_hashing.py",
    "description": "Unit tests for SHA256 hashing utilities and atomic writing to ensure deterministic byte-level outputs.",
    "category": "test"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI workflow that installs dependencies, runs formatting/linting (if configured), and executes the test suite on supported Python versions for determinism verification.",
    "category": "config"
  },
  {
    "path": "pyproject.toml",
    "description": "Project configuration defining dependencies, test tooling, and a console script entry (optional) to invoke src/run.py as the single CLI entrypoint.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documentation describing how to run the CLI tool, the deterministic guarantees, and the fixed output artifact schemas and locations.",
    "category": "documentation"
  }
]

