[
  {
    "path": "src/entrypoint.py",
    "description": "Single executable entrypoint that runs the pipeline normally or in --determinism mode by executing two runs with the same fixed seed, collecting artifact paths, and delegating checksum reporting to the determinism utilities.",
    "category": "source"
  },
  {
    "path": "src/determinism.py",
    "description": "Determinism helpers that run the pipeline twice with a fixed seed, compute sha256 checksums for outputs/results.json, outputs/run_stamp.json, and logs, and write outputs/determinism_report.json in a stable schema for CI verification.",
    "category": "source"
  },
  {
    "path": "tests/test_determinism.py",
    "description": "Pytest that invokes the single entrypoint determinism mode and asserts that the two runs produce identical sha256 checksums for results.json, run_stamp.json, and logs as recorded in outputs/determinism_report.json.",
    "category": "test"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI workflow that installs dependencies, runs unit tests including the determinism pytest, and fails the build if determinism checksums differ across the two fixed-seed runs.",
    "category": "config"
  }
]

