[
  {
    "path": "src/cli.py",
    "description": "Command-line entrypoint to run end-to-end hypothesis generation, robustness checks, and data-confrontation pipeline with reproducible configs.",
    "category": "source"
  },
  {
    "path": "src/core/conjectures.py",
    "description": "Defines swampland conjecture objects, parameterizations, priors, and consistency-check evaluators used to map theory constraints into model-level bounds.",
    "category": "source"
  },
  {
    "path": "src/core/models.py",
    "description": "Implements model-specific cosmology backends (inflationary/noninflationary, reheating, trans-Planckian, late-time dark energy) with observable calculators.",
    "category": "source"
  },
  {
    "path": "src/core/observables.py",
    "description": "Computes concrete cosmological observables (ns, r, running, non-Gaussianity amplitudes/shapes, reheating signatures, dark-energy evolution) from model trajectories.",
    "category": "source"
  },
  {
    "path": "src/core/holography.py",
    "description": "Encodes holographic/entropy/quantum-information-inspired constraints and maps them onto cosmological parameters and consistency tests.",
    "category": "source"
  },
  {
    "path": "src/core/mapping.py",
    "description": "Generates prioritized, testable conjecture→observable hypothesis pairs with explicit signatures, falsification/consistency tests, and ranking heuristics.",
    "category": "source"
  },
  {
    "path": "src/core/robustness.py",
    "description": "Runs robustness plans under realistic compactification/flux choices via scenario samplers, sensitivity analyses, and stress tests for hypothesis stability.",
    "category": "source"
  },
  {
    "path": "src/stats/pipeline.py",
    "description": "Provides the recommended statistical pipeline (likelihoods, priors, Bayesian/posterior + forecast hooks, model comparison) to confront current and forthcoming data.",
    "category": "source"
  },
  {
    "path": "src/lib/io.py",
    "description": "Handles configuration loading, schema validation, artifact saving (JSON/CSV), and experiment bookkeeping for reproducible runs.",
    "category": "source"
  },
  {
    "path": "src/lib/plotting.py",
    "description": "Generates publication-ready plots for hypothesis rankings, constraint projections, posterior summaries, and robustness diagnostics.",
    "category": "source"
  },
  {
    "path": "tests/test_smoke_pipeline.py",
    "description": "Smoke tests that execute the full pipeline on a small synthetic configuration and assert stable, correctly-shaped outputs and determinism.",
    "category": "test"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "CI workflow running linting, type checks, and pytest on pushes and pull requests to satisfy the required CI/CD setup.",
    "category": "config"
  }
]

