[
  {
    "path": "src/cosmo_contracts/__init__.py",
    "description": "Package initializer exposing the public API for benchmark contract generation, validation, and compliance reporting.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/schema.py",
    "description": "Defines the contract data model and JSON schema for required metadata, reference algorithm/pseudocode, output invariants, tolerance policy, and canonical test vectors.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/markdown.py",
    "description": "Parses and rewrites benchmarks_v0_1.md to insert or update a standardized 'Contract' section for each v0.1 benchmark idempotently.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/contracts.py",
    "description": "Implements contract generation defaults, normalization rules, and helpers to build canonical contract blocks for each benchmark.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/runner.py",
    "description": "Runs contributed benchmark implementations against the canonical test vectors and produces a pass/fail + diagnostics compliance report per contract.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/diagnostics.py",
    "description": "Provides structured diagnostics (diffs, tolerance mismatches, invariant violations) and utilities to serialize compliance results as JSON and human-readable text.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/cli.py",
    "description": "Command-line interface to (1) add/update contract sections in the v0.1 benchmark markdown and (2) validate implementation compliance and emit reports.",
    "category": "source"
  },
  {
    "path": "src/cosmo_contracts/__main__.py",
    "description": "Entry point enabling 'python -m cosmo_contracts' to invoke the CLI for contract injection and compliance checking.",
    "category": "source"
  },
  {
    "path": "tests/test_contract_injection.py",
    "description": "End-to-end tests ensuring contract sections are inserted/updated deterministically in benchmark markdown and include all required subsections.",
    "category": "test"
  },
  {
    "path": "tests/test_compliance_runner.py",
    "description": "Tests the compliance runner on stub implementations to verify pass/fail decisions, tolerance behavior, invariant checks, and diagnostic output stability.",
    "category": "test"
  },
  {
    "path": "docs/contract_spec_v0_1.md",
    "description": "Documentation specifying the v0.1 benchmark Contract section format, required fields, tolerance policy semantics, canonical test vector rules, and reporting requirements for contributors.",
    "category": "documentation"
  },
  {
    "path": "config/contract_defaults_v0_1.json",
    "description": "Default contract templates and per-benchmark overrides (metadata keys, invariants, tolerances, and canonical vectors) used to generate standardized Contract sections.",
    "category": "config"
  }
]

