    "path": "tools/schema_docs.py",
    "description": "CLI tool that generates Markdown documentation from schema.json and optionally regenerates schema stubs from documented examples to keep schema and docs in sync.",
    "category": "source"
  },
  {
    "path": "tools/validate_benchmark_outputs.py",
    "description": "CLI validator that discovers benchmark output JSON files, validates them against outputs/schemas/benchmark_schema.json, and fails with actionable error messages when violations or deprecated ad-hoc formats are found.",
    "category": "source"
  },
  {
    "path": "tools/output_format_policy.py",
    "description": "Shared policy module that defines allowed output file patterns, deprecated legacy/ad-hoc patterns, and mapping rules to schema versions used by the validator and CI/pre-commit hooks.",
    "category": "source"
  },
  {
    "path": "tools/schema_utils.py",
    "description": "Utilities for loading JSON Schema, resolving local $ref pointers, and running Draft 2020-12 validation with stable, readable error reporting.",
    "category": "support"
  },
  {
    "path": "docs/benchmark_output_schema.md",
    "description": "Generated documentation describing the benchmark output schema, required fields, examples, and migration notes for deprecated ad-hoc formats.",
    "category": "documentation"
  },
  {
    "path": ".pre-commit-config.yaml",
    "description": "Pre-commit configuration that runs benchmark output validation and blocks commits introducing invalid or deprecated benchmark output files.",
    "category": "config"
  },
  {
    "path": ".github/workflows/validate-benchmark-outputs.yml",
    "description": "GitHub Actions workflow that validates all benchmark outputs against schema.json on pull requests and pushes as a required CI gate.",
    "category": "config"
  },
  {
    "path": "pyproject.toml",
    "description": "Project configuration defining the tools package, dependencies (including jsonschema), and runnable console scripts for docs generation and validation.",
    "category": "config"
  },
  {
    "path": "tests/test_validate_benchmark_outputs.py",
    "description": "Test suite covering schema validation success/failure cases, deprecated format detection, and deterministic error output for the validator CLI.",
    "category": "test"
  },
  {
    "path": "tests/fixtures/valid_output.json",
    "description": "Representative valid benchmark output JSON fixture that conforms to outputs/schemas/benchmark_schema.json for validator and doc generation tests.",
    "category": "test"
  },
  {
    "path": "tests/fixtures/deprecated_output_legacy.json",
    "description": "Representative deprecated/ad-hoc benchmark output JSON fixture used to verify the validator blocks legacy formats not represented in schema.json.",
    "category": "test"
  }
]

