[
  {
    "path": "pyproject.toml",
    "description": "Defines the Python package metadata, dependencies, CLI entrypoint, formatting/linting tool settings, and pytest configuration for a reproducible pipeline.",
    "category": "config"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "Runs continuous integration on pushes and pull requests including linting, unit tests, and minimal smoke execution of the CLI pipeline.",
    "category": "config"
  },
  {
    "path": ".github/workflows/cd.yml",
    "description": "Implements continuous delivery by building and publishing a versioned Python package artifact (and optionally a GitHub Release) on tagged commits.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documents the mission, scientific scope, installation, CLI usage examples, data-model conventions, and how to run simulations and inference end-to-end.",
    "category": "documentation"
  },
  {
    "path": "src/dgpipe/__init__.py",
    "description": "Initializes the dgpipe package, exposes the public API surface, and centralizes version information.",
    "category": "source"
  },
  {
    "path": "src/dgpipe/cli.py",
    "description": "Provides a complete command-line interface to generate protocols, run simulations, and execute statistical inference with saved outputs and provenance metadata.",
    "category": "source"
  },
  {
    "path": "src/dgpipe/protocols.py",
    "description": "Implements a prioritized library of concrete measurement protocols for correlators and entanglement diagnostics across analogue platforms and astrophysical probes with machine-readable schemas.",
    "category": "source"
  },
  {
    "path": "src/dgpipe/models.py",
    "description": "Defines discrete-gravity-inspired effective models (e.g., causal-set/discrete-spectrum motivated propagators and dispersion relations) and their mappings to observable correlators.",
    "category": "source"
  },
  {
    "path": "src/dgpipe/simulations.py",
    "description": "Runs controlled numerical and semi-analytical simulations to quantify finite-size, discretization, and dispersive systematics for each protocol and produces synthetic datasets.",
    "category": "source"
  },
  {
    "path": "src/dgpipe/inference.py",
    "description": "Implements a statistical inference strategy (likelihoods, nuisance/systematics marginalization, Bayesian posterior sampling and/or frequentist fits) translating measured data into constraints on discrete-structure parameters.",
    "category": "source"
  },
  {
    "path": "src/dgpipe/io.py",
    "description": "Handles robust I/O for configs, datasets, results, and provenance (JSON/CSV/NPZ), including schema validation and deterministic seeding for reproducibility.",
    "category": "source"
  },
  {
    "path": "tests/test_pipeline_smoke.py",
    "description": "End-to-end smoke tests ensuring protocol generation, simulation, and inference run deterministically and produce valid outputs under CI constraints.",
    "category": "test"
  }
]

