from pathlib import Path
import json, textwrap, os, re, datetime
target_path = Path('/mnt/data').joinpath('schemas/benchmark.schema.json')
target_path.parent.mkdir(parents=True, exist_ok=True)

schema = {
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://example.org/schemas/benchmark.schema.json",
  "title": "Minimal Benchmark Run Schema",
  "type": "object",
  "additionalProperties": False,
  "required": ["schema_version", "run_id", "timestamp", "benchmark", "system", "results"],
  "properties": {
    "schema_version": {
      "type": "string",
      "description": "Version of this benchmark schema.",
      "pattern": r"^\d+\.\d+\.\d+$",
      "examples": ["1.0.0"]
    },
    "run_id": {
      "type": "string",
      "description": "Client-chosen identifier for this run (unique within a project).",
      "minLength": 1
    },
    "timestamp": {
      "type": "string",
      "description": "ISO 8601 timestamp when the run started (UTC recommended).",
      "format": "date-time"
    },
    "benchmark": {
      "type": "object",
      "additionalProperties": False,
      "required": ["name", "version"],
      "properties": {
        "name": {"type": "string", "minLength": 1},
        "version": {"type": "string", "minLength": 1},
        "description": {"type": "string"}
      }
    },
    "system": {
      "type": "object",
      "additionalProperties": False,
      "required": ["model", "runner"],
      "properties": {
        "model": {
          "type": "object",
          "additionalProperties": False,
          "required": ["name"],
          "properties": {
            "name": {"type": "string", "minLength": 1},
            "provider": {"type": "string"},
            "version": {"type": "string"},
            "parameters": {"type": "object", "additionalProperties": True}
          }
        },
        "runner": {
          "type": "object",
          "additionalProperties": False,
          "required": ["name", "version"],
          "properties": {
            "name": {"type": "string", "minLength": 1},
            "version": {"type": "string", "minLength": 1},
            "commit": {"type": "string"}
          }
        },
        "hardware": {
          "type": "object",
          "additionalProperties": True,
          "properties": {
            "cpu": {"type": "string"},
            "gpu": {"type": "string"},
            "ram_gb": {"type": "number", "minimum": 0}
          }
        },
        "os": {"type": "string"}
      }
    },
    "config": {
      "type": "object",
      "description": "Benchmark configuration used for the run; intentionally open-ended.",
      "additionalProperties": True
    },
    "results": {
      "type": "array",
      "minItems": 1,
      "items": {"$ref": "#/$defs/result"}
    },
    "summary": {
      "type": "object",
      "description": "Optional aggregated metrics for the whole run.",
      "additionalProperties": True
    },
    "notes": {"type": "string"}
  },
  "$defs": {
    "result": {
      "type": "object",
      "additionalProperties": False,
      "required": ["case_id", "status", "metrics"],
      "properties": {
        "case_id": {"type": "string", "minLength": 1},
        "status": {
          "type": "string",
          "description": "Outcome for this case.",
          "enum": ["pass", "fail", "error", "skip"]
        },
        "metrics": {
          "type": "object",
          "description": "Case-level metrics (scores, latencies, etc.).",
          "additionalProperties": {
            "anyOf": [
              {"type": "number"},
              {"type": "integer"},
              {"type": "string"},
              {"type": "boolean"},
              {"type": "null"}
            ]
          }
        },
        "output": {
          "type": ["object", "string", "null"],
          "description": "Optional raw model output for the case.",
          "additionalProperties": True
        },
        "error": {
          "type": "object",
          "description": "Present when status=error.",
          "additionalProperties": False,
          "required": ["message"],
          "properties": {
            "message": {"type": "string"},
            "type": {"type": "string"},
            "traceback": {"type": "string"}
          }
        },
        "started_at": {"type": "string", "format": "date-time"},
        "ended_at": {"type": "string", "format": "date-time"}
      },
      "allOf": [
        {
          "if": {"properties": {"status": {"const": "error"}}},
          "then": {"required": ["error"]}
        }
      ]
    }
  }
}

# build chunks as required: list of multiline strings (no leading/trailing blank lines)
text = json.dumps(schema, indent=2, ensure_ascii=False) + "\n"
chunks = [text.strip("\n")]
final_text = '\n'.join(block.strip('\n') for block in chunks).strip() + '\n'
target_path.write_text(final_text, encoding='utf-8')
print('FILE_WRITTEN:schemas/benchmark.schema.json')
print('DIR_STATE:' + json.dumps(sorted(str(p.relative_to(Path("/mnt/data"))) for p in target_path.parent.glob('*') if p.is_file())))
