[
  {
    "path": "src/determinism.py",
    "description": "Implements a deterministic-run policy that sets and validates fixed RNG seeds across Python/random, NumPy, and any supported frameworks and provides context managers to enforce determinism during benchmark runs.",
    "category": "source"
  },
  {
    "path": "src/stable_json.py",
    "description": "Provides stable JSON serialization utilities (canonical key ordering, normalized floats, consistent indentation/newlines) to ensure byte-for-byte reproducible output files across machines and CI.",
    "category": "source"
  },
  {
    "path": "src/numeric_tolerance_diff.py",
    "description": "Implements a numeric-tolerance comparison engine for expected-vs-actual JSON that supports absolute/relative tolerances, per-path overrides, NaN/inf handling, and produces a structured diff report.",
    "category": "source"
  },
  {
    "path": "src/benchmark_compare.py",
    "description": "Integrates deterministic policy, stable serialization, and tolerance-based numeric diffs into the existing expected-vs-actual comparison against expected/benchmark_case_001.expected.json and returns CI-friendly exit codes and reports.",
    "category": "source"
  },
  {
    "path": "scripts/run_benchmark_case_001.py",
    "description": "End-to-end runner script that executes benchmark_case_001 with fixed seed/settings, writes canonicalized actual output, and invokes the integrated comparator against the expected JSON.",
    "category": "support"
  },
  {
    "path": "config/benchmark_determinism_and_tolerance.json",
    "description": "Defines the single source of truth for RNG seeds, serialization options, and numeric tolerance thresholds (including optional per-field overrides) used locally and in CI.",
    "category": "config"
  },
  {
    "path": ".github/workflows/benchmark_ci.yml",
    "description": "CI workflow that runs the benchmark runner with the shared determinism/tolerance config, uploads diff artifacts on failure, and ensures consistent settings across CI environments.",
    "category": "config"
  },
  {
    "path": "tests/test_numeric_tolerance_diff.py",
    "description": "Unit tests covering tolerance comparisons (abs/rel), stable ordering, NaN/inf rules, and representative expected-vs-actual JSON diffs to prevent regressions in the harness.",
    "category": "test"
  }
]

