[
  {
    "path": "src/benchmarks/compare.py",
    "description": "Implements centralized comparison utilities supporting absolute/relative tolerances, per-field overrides, NaN/Inf handling, structured results, and rich mismatch reporting for benchmark outputs.",
    "category": "source"
  },
  {
    "path": "src/benchmarks/contract.py",
    "description": "Defines the benchmark contract schema (including per-observable tolerance specifications) and provides loaders/validators to ensure each task declares required metadata and tolerance choices.",
    "category": "source"
  },
  {
    "path": "src/benchmarks/runner.py",
    "description": "Runs benchmarks end-to-end, loads each task contract, executes candidate and reference algorithms, and enforces standardized comparisons using the centralized utilities with CI-friendly failure output.",
    "category": "source"
  },
  {
    "path": "tests/test_compare.py",
    "description": "Provides unit tests for comparison behavior including absolute/relative tolerance logic, per-field overrides, NaN/Inf policies, shape/type checking, and deterministic mismatch messages.",
    "category": "test"
  },
  {
    "path": "tests/test_runner_enforces_contract.py",
    "description": "Validates that the benchmark runner refuses missing/invalid tolerance documentation and correctly applies contract-defined tolerances when determining pass/fail.",
    "category": "test"
  },
  {
    "path": "docs/tolerances.md",
    "description": "Documents the tolerance policy, NaN handling rules, and a per-observable table template plus guidance for justifying tolerance choices for each v0.1 task.",
    "category": "documentation"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "Configures CI to run the benchmark runner and the test suite, failing the build on any contract validation error or comparison mismatch.",
    "category": "config"
  }
]

