    "category": "config"
  },
  {
    "path": ".pre-commit-config.yaml",
    "description": "Pre-commit configuration mirroring the intended CI checks to ensure consistent local and CI lint/format behavior.",
    "category": "config"
  },
  {
    "path": "requirements.txt",
    "description": "Pinned runtime Python dependencies used by CI and users to ensure deterministic installs on clean environments.",
    "category": "config"
  },
  {
    "path": "requirements-dev.txt",
    "description": "Pinned development and CI-only Python dependencies (linters, formatters, test tools) to keep CI deterministic and minimal.",
    "category": "config"
  },
  {
    "path": "RELEASE/CHECKLIST.md",
    "description": "Minimal release checklist documenting exactly how to prepare and tag v0.1, including CI verification steps and Git tag commands.",
    "category": "documentation"
  }
