    "category": "config"
  },
  {
    "path": ".github/workflows/e2e.yml",
    "description": "GitHub Actions workflow that runs the full pipeline via make e2e, uploads logs/artifacts, and triggers automatic issue creation on failures.",
    "category": "config"
  },
  {
    "path": "scripts/e2e.py",
    "description": "Python orchestrator that runs all pipeline stages, captures stdout/stderr and timing, writes structured logs/artifacts, and emits a failure summary for CI.",
    "category": "source"
  },
  {
    "path": "scripts/artifacts.py",
    "description": "Artifact utilities to package logs/results (e.g., tar/zip), redact secrets, and produce a deterministic artifact manifest for upload and debugging.",
    "category": "source"
  },
  {
    "path": "scripts/issue_on_failure.py",
    "description": "GitHub issue automation script that reads the failure summary, generates minimal reproduction steps, and opens or updates deduplicated issues via the GitHub API.",
    "category": "source"
  },
  {
    "path": "scripts/repro_steps.py",
    "description": "Generates minimal reproduction commands and environment details from pipeline context and failing stage metadata.",
    "category": "source"
  },
  {
    "path": ".github/ISSUE_TEMPLATE/e2e_failure.yml",
    "description": "Issue template for automated E2E failures including required fields, repro steps, logs/artifact links, and environment metadata.",
    "category": "config"
  },
  {
    "path": "scripts/README_e2e.md",
    "description": "Documentation describing how to run make e2e locally and in CI, how artifacts/logs are produced, and how failure issues are created and deduplicated.",
    "category": "documentation"
  },
  {
    "path": "scripts/requirements-ci.txt",
    "description": "Pinned minimal Python dependencies for CI automation (requests/pyyaml etc.) used by the e2e runner and issue automation scripts.",
    "category": "config"
  }
]

