[
  {
    "path": "Dockerfile",
    "description": "Defines a reproducible container image that installs pinned Python dependencies from a lockfile and runs the project deterministically in CI and locally.",
    "category": "config"
  },
  {
    "path": ".dockerignore",
    "description": "Excludes caches, virtual environments, build artifacts, and large/unnecessary files from the Docker build context to keep images reproducible and small.",
    "category": "config"
  },
  {
    "path": "pyproject.toml",
    "description": "Declares the project metadata, Python version constraints, dependencies, and tooling configuration using Poetry for reproducible builds.",
    "category": "config"
  },
  {
    "path": "poetry.lock",
    "description": "Pins all transitive dependency versions and hashes to ensure fully reproducible installs across environments and CI runs.",
    "category": "config"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "Runs lint/test/benchmark reproducibility checks using both host Python and the Docker image to gate the v0.1.0 release on green CI.",
    "category": "config"
  },
  {
    "path": "scripts/record_benchmark_metadata.py",
    "description": "Writes benchmark output metadata (Python version, platform, package versions, git commit/tag, and timestamp) into a JSON sidecar or embedded section for reproducibility.",
    "category": "support"
  },
  {
    "path": "scripts/release_v0_1_0.sh",
    "description": "Creates and verifies a reproducible v0.1.0 git tag only when the working tree is clean and CI checks have passed.",
    "category": "support"
  }
]

